/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
atom-text-editor .gutter .line-number.git-line-modified {
  border-left: 2px solid #ffc722;
  padding-left: calc(0.5em - 2px);
}
atom-text-editor .gutter .line-number.git-line-added {
  border-left: 2px solid #09c003;
  padding-left: calc(0.5em - 2px);
}
atom-text-editor .gutter .line-number.git-line-removed:before {
  position: absolute;
  left: 0;
  bottom: -4px;
  height: 0;
  width: 0;
  content: " ";
  border: solid transparent;
  border-left-color: #d73838;
  border-width: 4px;
  margin-top: -4px;
  pointer-events: none;
}
atom-text-editor .gutter.git-diff-icon .line-number {
  width: 100%;
  border-left: none;
  padding-left: 0.4em;
}
atom-text-editor .gutter.git-diff-icon .line-number:before {
  font-family: 'Octicons Regular';
  src: url("octicons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  position: relative;
  top: -0.05em;
  height: 0px;
  width: 1em;
  content: " ";
  padding-right: 0.4em;
  font-size: .95em;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-modified:before {
  content: "\f052";
  color: #ffc722;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-added:before {
  content: "\f05d";
  color: #09c003;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-removed:before {
  border: none;
  content: "\f0ca";
  color: #d73838;
  position: relative;
  top: .6em;
}
