css:
.transition-03 {
  transition: 0.3s ease;
}
body {
  font-size: 12px;
  color: #212529;
  background-color: #fff;
}
atom-pane {
  border-right: 1px solid #f1f3f5;
}
atom-pane:last-child {
  border-right: none;
}
atom-pane-container atom-pane .item-views > * {
  top: 5px;
  bottom: 5px;
}
.icon::before {
  position: relative;
  top: 1px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar {
  width: 5px;
  height: 5px;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb {
  border-radius: 5px;
  border: 2px solid transparent;
  background: #f1f3f5;
  background-clip: content-box;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb:vertical:active {
  border-radius: 0;
  border-left-width: 0;
  border-right-width: 0;
}
.scrollbars-visible-always /deep/ ::-webkit-scrollbar-thumb:horizontal:active {
  border-radius: 0;
  border-top-width: 0;
  border-bottom-width: 0;
}
.btn {
  color: #2f343a;
  background-color: #ffffff;
}
.btn:focus,
.btn:hover {
  color: #212529;
  background-color: #ffffff;
}
.btn:active {
  color: rgba(33, 37, 41, 0.66);
  background-color: #fafafa;
}
.btn.selected {
  color: #212529;
  background-color: #ffffff;
}
.btn:focus,
.btn:focus:active {
  outline: none;
}
.btn .icon::before {
  position: relative;
  top: 1px;
}
.btn.btn-primary {
  color: #212529;
  background-color: #212529;
}
.btn.btn-primary:focus,
.btn.btn-primary:hover {
  background-color: #2a2f34;
}
.btn.btn-primary:active {
  background-color: #181b1e;
}
.btn.btn-primary.selected {
  background-color: #2a2f34;
}
.btn.btn-primary.selected:focus,
.btn.btn-primary.selected:hover {
  background-color: #333940;
}
.btn.btn-info {
  color: #212529;
  background-color: #228ae6;
}
.btn.btn-info:focus,
.btn.btn-info:hover {
  background-color: #3494e8;
}
.btn.btn-info:active {
  background-color: #1980db;
}
.btn.btn-info.selected {
  background-color: #3494e8;
}
.btn.btn-info.selected:focus,
.btn.btn-info.selected:hover {
  background-color: #479dea;
}
.btn.btn-success {
  color: #212529;
  background-color: #212529;
}
.btn.btn-success:focus,
.btn.btn-success:hover {
  background-color: #2a2f34;
}
.btn.btn-success:active {
  background-color: #181b1e;
}
.btn.btn-success.selected {
  background-color: #2a2f34;
}
.btn.btn-success.selected:focus,
.btn.btn-success.selected:hover {
  background-color: #333940;
}
.btn.btn-warning {
  color: #212529;
  background-color: #fd7e14;
}
.btn.btn-warning:focus,
.btn.btn-warning:hover {
  background-color: #fd8928;
}
.btn.btn-warning:active {
  background-color: #fa7302;
}
.btn.btn-warning.selected {
  background-color: #fd8928;
}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #fd943c;
}
.btn.btn-error {
  color: #212529;
  background-color: #fa5252;
}
.btn.btn-error:focus,
.btn.btn-error:hover {
  background-color: #fb6666;
}
.btn.btn-error:active {
  background-color: #f93e3e;
}
.btn.btn-error.selected {
  background-color: #fb6666;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #fb7a7a;
}
.btn-group > .btn {
  transition: 0.3s ease;
  border-color: transparent;
}
.btn-group > .btn:hover {
  background: #fff;
}
body {
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
.is-blurred {
  -webkit-filter: grayscale();
}
atom-pane {
  padding: 35px 25px;
}
atom-text-editor[mini] {
  padding-left: 5px;
  font-size: .9rem;
  color: #212529;
  background-color: #fff;
  border-bottom: 1px solid transparent;
}
atom-text-editor[mini].is-focused {
  border-bottom: 1px solid #f1f3f5;
}
atom-text-editor[mini].is-focused .cursor {
  display: block;
}
atom-text-editor[mini] .placeholder-text {
  color: #868e96;
}
atom-text-editor[mini] .selection .region {
  background-color: #ffffff;
}
atom-text-editor[mini] .cursor {
  display: none;
}
.find-and-replace {
  border-top: 1px solid #f1f3f5;
}
.status {
  color: #212529;
}
.status-added {
  color: #212529;
}
.status-ignored {
  color: #868e96;
}
.status-modified {
  color: #12b886;
}
.status-removed {
  color: #fa5252;
}
.status-renamed {
  color: #228ae6;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 5px;
  line-height: 2;
  font-family: 'Helvetica Neue', Arial, sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 2px;
  background-color: #fff;
}
.list-group li {
  padding-left: 5px;
  border-bottom: 1px dotted #f1f3f5;
  color: #adb5bd;
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
.list-group li.selected {
  color: #212529;
  z-index: 15;
}
.list-group li.selected:before {
  display: none;
}
.list-tree .selected {
  color: #212529;
}
.select-list li {
  padding: 7.5px 10px;
}
.select-list .status {
  float: right;
}
.select-list .character-match {
  font-weight: normal !important;
  border-bottom: 1px solid #72c3fc;
  color: inherit;
}
autocomplete-suggestion-list.select-list.popover-list {
  padding: 5px;
  border-radius: 2px;
  background-color: #fff;
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description {
  background: transparent;
  font-size: .9rem;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description-content {
  font-size: .9rem;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li {
  line-height: 2em;
}
autocomplete-suggestion-list.select-list.popover-list .selected {
  background: #f8f9fa;
}
autocomplete-suggestion-list.select-list.popover-list .selected .icon {
  color: #212529;
}
.select-list .selected .secondary-line {
  color: #868e96;
}
.list-group li:not(.list-nested-item) {
  line-height: 1.8;
}
autocomplete-suggestion-list .suggestion-list-scroller {
  background: white;
}
autocomplete-suggestion-list .left-label {
  color: #868e96;
}
autocomplete-suggestion-list .word-container {
  color: #adb5bd;
}
autocomplete-suggestion-list .icon-container .icon {
  background: rgba(255, 255, 255, 0.5) !important;
  color: #ced4da;
}
.panel-heading {
  border-bottom: 1px solid #f1f3f5;
  background-color: #fff;
}
.inset-panel {
  background-color: #fff;
}
.inset-panel .panel-heading {
  background-color: #ffffff;
}
atom-panel.modal {
  position: absolute;
  border: 1px solid #f1f3f5;
  border-top: none;
  top: 0;
  left: 0;
  margin-left: 0;
  width: 100%;
  height: 100%;
  z-index: 100;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(255, 255, 255, 0.95);
}
atom-panel.modal > .select-list {
  min-width: 500px;
}
.inset-panel {
  border-radius: 0;
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  display: block;
  width: 16px;
  height: 16px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.settings-view .link {
  color: #212529;
}
.settings-view .link:hover {
  color: #212529;
}
.settings-view .alert {
  border: none;
}
.settings-view .config-menu {
  background: #fff;
}
.settings-view .config-menu .panels-menu li a {
  transition: 0.3s ease;
}
.settings-view .config-menu .panels-menu li.active a {
  background: #ffffff;
}
.settings-view .config-menu .panels-menu li:hover {
  background: #ffffff;
}
.settings-view .package-card {
  transition: 0.3s ease;
  background: #ffffff;
  border: none;
}
.settings-view .package-card:hover {
  background: #ffffff;
}
.ui-site-1 {
  background-color: #0088ff;
}
.ui-site-2 {
  background-color: #20b684;
}
.ui-site-3 {
  background-color: #cc8533;
}
.ui-site-4 {
  background-color: #D831B0;
}
.ui-site-5 {
  background-color: #EBDD5B;
}
.status-bar {
  font-size: 12px;
  color: #212529;
  border-top: 1px solid #f1f3f5;
  background-color: #fff;
}
.footer {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  transition: opacity 150ms ease-out;
  opacity: 0;
}
.footer:hover {
  opacity: 1;
}
.title {
  text-transform: uppercase;
  font-size: 10px;
  letter-spacing: 1px;
}
.transition {
  transition: all 125ms cubic-bezier(0.215, 0.61, 0.355, 1);
}
.small-shadow {
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
.medium-shadow {
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.1), 0 0 1px 0 rgba(0, 0, 0, 0.14);
}
.large-shadow {
  box-shadow: 0 5px 32px 0 rgba(0, 0, 0, 0.1);
}
atom-pane:not(.active) .tab-bar .tab.active {
  border-top: 1px solid transparent;
  color: #868e96;
}
.platform-darwin atom-workspace atom-panel-container.left:empty + atom-workspace-axis.vertical .tab-bar {
  padding-left: 0 !important;
}
.tab-bar {
  background-color: #fff;
  border-top: 1px solid #f1f3f5;
  margin: 15px;
  text-transform: uppercase;
  font-size: 10px;
  letter-spacing: 1px;
}
.tab-bar .tab {
  color: #868e96;
  height: 34px;
  line-height: 34px;
  font-size: 12px;
  padding-right: 10px;
  border-top: 1px solid transparent;
  margin-top: -1px;
  max-width: 130px;
  text-transform: uppercase;
  font-size: 11px;
}
.tab-bar .tab:last-child {
  border-right: none;
}
.tab-bar .tab .title .icon::before {
  font-size: 13px;
}
.tab-bar .tab.active {
  flex: 1;
  color: #212529;
  border-top: 1px solid #212529;
  font-weight: bold;
}
.tab-bar .tab.active .title {
  padding: 0;
}
.tab-bar .tab.modified .title {
  color: #212529;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 50%;
  right: 14px;
  margin-top: -3px;
  border-color: #228ae6;
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon {
  opacity: 0;
}
.tab-bar .tab .close-icon::before {
  text-align: center;
}
.tab-bar .tab .close-icon:hover {
  color: #212529;
}
.tab-bar .tab.active .close-icon:hover {
  color: #212529;
}
.tab-bar .tab.is-dragging {
  background: #e6e6e6;
  border-color: transparent;
  opacity: 0.5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.tab-bar .tab:hover .close-icon {
  opacity: 1;
}
.text-smaller {
  font-size: smaller;
}
.text-subtle {
  color: #868e96;
}
.text-highlight {
  color: #212529;
}
.text-info {
  color: #228ae6;
}
.text-success {
  color: #212529;
}
.text-warning {
  color: #12b886;
}
.text-error {
  color: #fa5252;
}
.highlight {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #ffffff;
  color: #212529;
}
.highlight-info {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #228ae6;
  color: #212529;
}
.highlight-warning {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #fd7e14;
  color: #212529;
}
.highlight-error {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #fa5252;
  color: #212529;
}
.highlight-success {
  padding: 1px 4px;
  border-radius: 2px;
  background-color: #212529;
  color: #212529;
}
.background-message {
  font-weight: bold;
  color: #868e96;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: 0.75em;
  white-space: nowrap;
  max-width: none;
  color: #FFF;
}
.tooltip .keystroke {
  padding: 0.15em 0.4em;
  margin: 0 -0.3em 0 0.25em;
  color: #FFF;
  background: #ffffff;
}
.tree-view-resizer {
  border-top: 1px solid #f1f3f5;
  border-right: 1px solid #f1f3f5;
}
.tree-view {
  color: #212529;
  background-color: #fff;
}
.tree-view .list-item.selected {
  color: #212529;
}
.tree-view .list-item.selected::before {
  background: #ffffff;
}
@keyframes show-logo {
  from {
    opacity: 0;
    transform: scale(0.9, 0.9);
  }
  to {
    opacity: 1;
    transform: scale(1, 1);
  }
}
.background-message {
  transform: translateY(-25px);
}
.background-message::before {
  position: absolute;
  display: block;
  content: '';
  background: url('data:image/svg+xml;base64,') 50% 50% no-repeat;
  background-size: 10%;
  height: 100%;
  width: 100%;
  top: 0;
  left: 0;
  opacity: 0;
  animation: show-logo 5s ease-out forwards;
}
.background-message .message {
  display: none;
}
/* is the plugin still necessary? */
.no-title-bar:not(.fullscreen).custom-title-bar > atom-workspace atom-panel-container.left,
.no-title-bar:not(.fullscreen).custom-title-bar > atom-workspace atom-panel-container.right {
  margin-top: 0;
}
atom-dock .left atom-pane {
  border-right: 1px solid #f1f3f5;
}
atom-dock .right atom-pane {
  border-left: 1px solid #f1f3f5;
}
atom-dock .bottom atom-pane {
  border-top: 1px solid #f1f3f5;
}
