/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
.github-CommitView {
  padding: 10px;
  border-top: 1px solid #e0dacc;
  flex: 0 0 auto;
}
.github-CommitView-editor {
  position: relative;
  border: 1px solid #e0dacc;
  border-radius: 3px;
  padding: 5px;
  font-size: 12px;
  background-color: #f8f6f1;
}
.github-CommitView-editor atom-text-editor {
  height: 100px;
}
.github-CommitView-editor atom-text-editor .cursor-line.cursor-line.cursor-line {
  background-color: transparent;
}
.github-CommitView-bar {
  display: flex;
  align-items: center;
  margin-top: 10px;
}
.github-CommitView-label {
  margin-right: 10px;
}
.github-CommitView-button {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
}
.github-CommitView-button.is-secondary {
  flex: 0 1 auto;
  margin-right: 10px;
}
.github-CommitView-remaining-characters {
  width: 1.25em;
  text-align: right;
  margin-left: 10px;
  color: #a6956d;
}
.github-CommitView-remaining-characters.is-warning {
  color: #d5880b;
}
.github-CommitView-remaining-characters.is-error {
  color: #f42a2a;
}
.theme-one-dark-ui .github-CommitView-editor atom-text-editor.is-focused,
.theme-one-light-ui .github-CommitView-editor atom-text-editor.is-focused {
  box-shadow: none;
}
.theme-one-dark-ui .github-CommitView-editor atom-text-editor.is-focused:before,
.theme-one-light-ui .github-CommitView-editor atom-text-editor.is-focused:before {
  content: "";
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  border: 2px solid;
  border-color: inherit;
  border-radius: 3px;
}
