css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
.github-PrPaneItem {
  padding: 30px;
  overflow: auto;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  font-size: 1.25em;
  line-height: 1.5;
  background-color: #f7f6f2;
}
.github-PrPaneItem * {
  user-select: all;
  -webkit-user-select: all;
}
.github-PrPaneItem-container {
  max-width: 48em;
  margin: 0 auto;
}
.github-PrPaneItem .issueish-badge-and-link {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
}
.github-PrPaneItem .issueish-badge {
  align-self: flex-start;
  flex-shrink: 0;
  margin-right: 10px;
  font-weight: bold;
  text-transform: capitalize;
  border-radius: 3px;
}
.github-PrPaneItem .issueish-badge.open {
  color: white;
  background-color: #28a745;
}
.github-PrPaneItem .issueish-badge.closed {
  color: white;
  background-color: #dc3545;
}
.github-PrPaneItem .issueish-badge.merged {
  color: white;
  background-color: #6f42c1;
}
.github-PrPaneItem .issueish-link a {
  color: #a6956d;
}
.github-PrPaneItem .issueish-avatar-and-title {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-bottom: 1px solid #e0dacc;
}
.github-PrPaneItem .author-avatar-link {
  margin-right: 10px;
  align-self: flex-start;
}
.github-PrPaneItem .author-avatar {
  max-height: 32px;
  border-radius: 3px;
}
.github-PrPaneItem .issueish-title {
  margin: 0;
  font-size: 1.5em;
  font-weight: 500;
  line-height: 1.3;
  color: #262117;
}
.github-PrPaneItem .issueish-body {
  padding-top: 10px;
  border-top: 1px solid #e0dacc;
}
.github-PrPaneItem .issueish-body > *:first-child {
  margin-top: 0;
}
.github-PrPaneItem .github-DotComMarkdownHtml a {
  color: #1492ff;
}
.github-PrPaneItem .github-DotComMarkdownHtml p {
  line-height: 1.5;
}
.github-PrPaneItem .github-DotComMarkdownHtml hr {
  margin-top: 5px;
  margin-bottom: 5px;
  border-top: 1px solid #4c432e;
  width: 100%;
}
.github-PrPaneItem .github-DotComMarkdownHtml ul {
  margin-left: -10px;
}
.github-PrPaneItem .github-DotComMarkdownHtml ol {
  margin-left: -20px;
}
.github-PrPaneItem .github-DotComMarkdownHtml li {
  font-size: .9em;
  padding-top: .1em;
  padding-bottom: .1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml li li {
  font-size: 1em;
}
.github-PrPaneItem .github-DotComMarkdownHtml pre > code {
  white-space: pre;
}
.github-PrPaneItem .reactions {
  width: fit-content;
  padding-top: 10px;
}
.github-PrPaneItem .reactions .reaction-group {
  margin-right: 10px;
  padding: 10px;
  border: 1px solid #e0dacc;
  border-radius: 3px;
}
.github-PrPaneItem .reactions:empty {
  all: unset;
}
.github-PrPaneItem blockquote {
  font-size: inherit;
  padding-top: 0;
  padding-left: 10px;
  padding-bottom: 0;
  color: #a6956d;
  border-left: 2px solid #a6956d;
}
