.github-StatusBarTileController {
  display: inline-block;
}
.github-StatusBarTileController-tooltipMenu.tooltip {
  width: 400px;
  padding-left: 10px;
  padding-right: 10px;
  box-sizing: border-box;
}
.github-StatusBarTileController-tooltipMenu .tooltip-inner.tooltip-inner {
  padding: 5px;
  background-color: #ebe8df;
  border: 1px solid #e0dacc;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  color: #4c432e;
}
.github-StatusBarTileController-tooltipMenu.top .tooltip-arrow.tooltip-arrow {
  width: 10px;
  height: 10px;
  border-width: 0 0 1px 1px;
  border-color: #e0dacc;
  background-color: #ebe8df;
  border-bottom-right-radius: 2px;
  transform: rotate(-45deg);
}
.github-StatusBarTileController > .inline-block:hover {
  text-decoration: underline;
  cursor: default;
}
.github-StatusBarTileController .icon-sync::before {
  animation: github-StatusBarSync-animation 2s linear 30;
}
@keyframes github-StatusBarSync-animation {
  100% {
    transform: rotate(360deg);
  }
}
.github-StatusBarTileController .github-ChangedFilesCount .icon-alert {
  margin-left: 5px;
}
.github-StatusBarTileController .github-branch-detached {
  color: #a6956d;
  font-style: italic;
}
