/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
.github-Tabs-Panel.active {
  display: flex;
}
.github-Tabs-Panel.inactive {
  display: none;
}
.github-Tabs.sidebar-tabs {
  height: 100%;
  display: flex;
  flex-direction: column;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer {
  display: flex;
  flex-direction: row;
  padding: 10px;
  border-bottom: 1px solid #e0dacc;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer .github-Tabs-NavigationItem {
  flex: 1;
  text-align: center;
  cursor: pointer;
  background-color: #ffffff;
  color: rgba(0, 0, 0, 0.6);
  border: 1px solid #e0dacc;
  padding: .25em .5em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer .github-Tabs-NavigationItem + .github-Tabs-NavigationItem {
  border-left: none;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer .github-Tabs-NavigationItem.active {
  background-color: #cdb066;
  color: #000000;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer .github-Tabs-NavigationItem:first-child {
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.github-Tabs.sidebar-tabs .github-Tabs-NavigationContainer .github-Tabs-NavigationItem:last-child {
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.github-Tabs.sidebar-tabs[data-tabs-count="1"] .github-Tabs-NavigationContainer {
  display: none;
}
.github-Tabs.sidebar-tabs .github-Tabs-PanelContainer {
  flex: 1;
  display: flex;
  flex-direction: column;
}
.github-Tabs.sidebar-tabs .github-Tabs-Panel {
  flex: 1;
}
