/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
status-bar {
  display: block;
  font-size: 11px;
  line-height: 22px;
  height: 26px;
  position: relative;
  -webkit-user-select: none;
  cursor: default;
  overflow: hidden;
  white-space: nowrap;
  min-width: -webkit-min-content;
}
status-bar a,
status-bar a:hover {
  color: #4c432e;
}
status-bar .flexbox-repaint-hack {
  padding: 0 12.5px;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
}
status-bar .status-bar-left {
  flex: 1 1 33%;
}
status-bar .status-bar-right {
  padding-left: 10px;
}
status-bar .status-bar-right .inline-block {
  margin-right: 0;
  margin-left: 10px;
}
status-bar .status-bar-right .inline-block > .inline-block {
  margin-left: 0;
}
status-bar .status-bar-left,
status-bar .status-bar-right {
  overflow-x: auto;
}
status-bar .status-bar-left::-webkit-scrollbar,
status-bar .status-bar-right::-webkit-scrollbar {
  display: none;
}
status-bar .inline-block {
  max-width: 20vw;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
status-bar .inline-block.file-info {
  max-width: none;
}
status-bar .file-info.buffer-modified::after {
  content: '*';
}
status-bar .icon:before {
  font-size: 14px;
  width: 14px;
  height: 14px;
  position: relative;
  top: 1px;
  margin-right: 0px;
}
status-bar .icon-diff-ignored:before,
status-bar .commits-ahead-label:before,
status-bar .commits-behind-label:before {
  margin-right: -1px;
}
status-bar .hide,
status-bar .current-path:empty,
status-bar .cursor-position:empty,
status-bar .selection-count:empty {
  display: none;
}
status-bar .git-view {
  display: inline-block;
}
status-bar .cursor-position a,
status-bar .cursor-position a:hover {
  color: #4c432e;
}
